import styled from "styled-components";

import tile from "./tile.png";

type Props = {
  x: number;
  y: number;
  size: {
    width: number;
    height: number;
  };
};

export const Container = styled.div.attrs((props: Props) => ({
  style: {
    width: props.size.width,
    height: props.size.height,
    marginLeft: props.x,
    marginTop: props.y,
  },
}))<Props>`
  background-image: url(${tile});
  background-size: cover;
  background-repeat: no-repeat;
  image-rendering: pixelated;

  position: absolute;
  transition: 0.3s;

  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

  /* Shape of tile */
  clip-path: polygon(50% 0, 100% 39%, 100% 60%, 50% 100%, 0 61%, 1% 36%);

  p {
    margin-top: -${({ size: { height } }) => height * 0.2}px;
    font-size: 8px;
    color: #fff;
  }

  &:hover {
    p {
      color: red;
    }
  }
`;
