import styled from "styled-components";

import tile from "./tile.png";

export const Container = styled.div.attrs((props: IIsometricTile) => ({
  style: {
    width: props.size.width,
    height: props.size.height,
    marginLeft: props.x,
    marginTop: props.y,
  },
}))<IIsometricTile>`
  box-shadow: 1px 1px 1px 1px rgba(255, 255, 255, 0.8);
`;

export const Tile = styled.div.attrs((props: IIsometricTile) => ({}))<
  IIsometricTile
>`
  background-image: url(${tile});
  background-size: cover;
  background-repeat: no-repeat;
  image-rendering: pixelated;

  position: absolute;
  transition: 1s;

  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;

  cursor: pointer;

  /* Shape of tile */
  clip-path: polygon(50% 0, 100% 39%, 100% 60%, 50% 100%, 0 61%, 1% 36%);

  &:hover {
    margin-top: ${({ y, tall }) => y - tall * 2}px !important;
  }
`;
