import React, { useMemo } from "react";

import { Container } from "./styles";

const IsometricTile: React.FC<IsometricTileType> = ({ x, y, size }) => {
  const posX = useMemo(() => {
    let newX = x * size;

    if (y % 2) {
      newX -= size / 2;
    }

    return newX;
  }, [size, x, y]);

  const posY = useMemo(() => {
    let newY = y * size;

    if (y % 2) {
      newY -= size / 2;
    }

    return newY;
  }, [size, y]);

  return <Container x={posX} y={posY} />;
};

export default IsometricTile;
